			topics_member.category = topics_member.safety_topic.category;
			topics_member.review_priority = topics_member.safety_topic.review_priority;
			
			// detect all change : is that topics still exist in the therapy ?
			var topicsStillPresent=false;
			for (var k in therapy.topics)
			{
				if (therapy.topics[ k ].name === topics_member.name)
					topicsStillPresent=true;
			}
			console.log("Topic["+ topics_member.name+"] stillPresent ? "+topicsStillPresent+ " in theraryList "+angular.toJson(therapy.topics));
			if (topicsStillPresent===false)
			{
				topics_member.message = "This topics is not present anymore in the therapy";
				topics_member.color="red";
			}
		}
		console.log(" search new topic now");
		
		// search new topic now 		
		for (var k in therapy.topics)
		{
			var topicsPresent=false;
			for (var j in meeting_th_to.topics_meeting)
			{
				if (therapy.topics[ k ].name === meeting_th_to.topics_meeting[ j ].name)
					topicsPresent=true;
				
			}
			console.log("Therapy Topic["+ therapy.topics[ k ].name+"] topicsAlreadyPresent? "+topicsPresent);
			if (topicsPresent === false)
			{
				// add it in the list of topics now
				var newTopics= JSON.parse(JSON.stringify(therapy.topics[ k ]));
				newTopics.safety_topic = JSON.parse(JSON.stringify( therapy.topics[ k ]));
