// Review Priority in the order High, Medium, Low, Not Defined.  
// Within each  Review Priority the topics can be sorted alphabetically.
function comparatorTopics(a,b) {
  var pa = getPriorityValue( a.review_priority );
  var pb = getPriorityValue( b.review_priority );
  console.log("Comparator topics ["+a.name+" ("+pa+")]<>["+b.name+" ("+pb+")] ");
  
  if (pa == pb)
	return a.name - b.name;
  return pa-pb;
}

function getPriorityValue( review_priority )
{
	if (review_priority === "HIGH")
		return 0;
	if (review_priority === "MED")
		return 1;
	if (review_priority === "LOW")
		return 2;
	if (review_priority === "UND")
		return 3;
	return 4;
}

